// src/components/Header.jsx
import React, { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';

export default function Header({ onFAQClick }) {
  const { pathname } = useLocation();
  const navigate = useNavigate();

  const isChatPage = pathname === '/chat';
  const showNotification = ['/', '/patient'].includes(pathname);
  const [showNotifications, setShowNotifications] = useState(false);

  const toggleNotifications = () => setShowNotifications(prev => !prev);

  return (
    <header style={{
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'space-between',
      height: '64px',
      padding: '0 1rem',
      borderBottom: '1px solid #ddd',
      background: '#fff',
      boxSizing: 'border-box',
      fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
      position: 'relative',
    }}>
      {/* 홈으로 이동 */}
      <h1
        style={{
          margin: 0,
          fontSize: '1.25rem',
          cursor: 'pointer',
          fontWeight: 600,
          color: '#333'
        }}
        onClick={() => navigate('/')}
      >
        AI Care
      </h1>

      {/* 오른쪽 버튼 영역 */}
      <div style={{ display: 'flex', gap: '8px' }}>
        {isChatPage && (
          <button
            style={buttonStyle}
            onClick={onFAQClick}
            aria-label="FAQ"
          >
            ?
          </button>
        )}

        {showNotification && (
          <button
            style={buttonStyle}
            onClick={toggleNotifications}
            aria-label="Notifications"
          >
            🔔
          </button>
        )}
      </div>

      {/* 알림창 */}
      {showNotifications && (
        <div style={notificationBoxStyle}>
          <div style={{ padding: '10px', fontWeight: 'bold' }}>알림</div>
          <ul style={{ margin: 0, padding: '0 10px 10px', listStyle: 'none', fontSize: '14px' }}>
            <li>🩺 새로운 환자 기록이 업데이트되었습니다</li>
            <li>📝 피드백이 도착했습니다</li>
            <li>📅 다음 상담 일정이 등록되었습니다</li>
          </ul>
        </div>
      )}
    </header>
  );
}

// 버튼 공통 스타일
const buttonStyle = {
  width: 40,
  height: 40,
  fontSize: '1.25rem', // 약 20px
  borderRadius: '50%',
  border: 'none',
  background: '#f5f5f5',
  fontWeight: 'bold',
  cursor: 'pointer',
  color: '#333'
};

// 알림창 스타일
const notificationBoxStyle = {
  position: 'absolute',
  top: '64px',
  right: '1rem',
  width: '240px',
  background: '#fff',
  border: '1px solid #ccc',
  borderRadius: '8px',
  boxShadow: '0 2px 8px rgba(0, 0, 0, 0.1)',
  zIndex: 1000,
};
