// src/pages/LoginPage.jsx
import React, { useEffect, useState } from "react";

const API_BASE = "http://localhost:8001";

export default function LoginPage() {
  const [status, setStatus] = useState("idle");
  const [me, setMe] = useState(null);

  // 로그인 성공 감지
  useEffect(() => {
    const q = new URLSearchParams(window.location.search);
    if (q.get("login") === "success") {
      setStatus("success");
      fetch(`${API_BASE}/profile`, { credentials: "include" })
        .then((r) => r.text())
        .then((t) => setMe(t))
        .catch(() => {});
    }
  }, []);

  const handleKakaoLogin = () => {
    const next = window.location.pathname || "/";
    window.location.href = `${API_BASE}/authorize?next=${encodeURIComponent(next)}`;
  };

  return (
    <div style={styles.wrap}>
      <div style={styles.card}>
        <h2>로그인</h2>
        <p style={{ color: "#666" }}>카카오로 로그인하세요.</p>
        <button onClick={handleKakaoLogin} style={styles.kakaoBtn}>
          카카오로 로그인
        </button>
        {status === "success" && (
          <div style={{ marginTop: 16 }}>
            <span style={{ color: "green", fontWeight: 600 }}>로그인 성공</span>
            <pre style={styles.pre}>{me || "사용자 정보를 불러오는 중..."}</pre>
          </div>
        )}
      </div>
    </div>
  );
}

const styles = {
  wrap: {
    minHeight: "100vh",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    background: "#f7f7f9",
  },
  card: {
    width: 360,
    background: "#fff",
    borderRadius: 16,
    boxShadow: "0 8px 24px rgba(0,0,0,0.08)",
    padding: 24,
  },
  kakaoBtn: {
    width: "100%",
    padding: "12px 14px",
    borderRadius: 10,
    border: "1px solid #FEE500",
    background: "#FEE500",
    fontWeight: 700,
    cursor: "pointer",
    marginTop: 12,
  },
  pre: {
    marginTop: 8,
    background: "#fafafa",
    border: "1px solid #eee",
    borderRadius: 8,
    padding: 8,
    fontSize: 12,
    maxHeight: 200,
    overflow: "auto",
  },
};
