import React, { useState, useRef, useEffect } from 'react';
import Header from '../components/Header';
import FAQModal from '../components/FAQModal';

const ChatBotPage = () => {
  const [isFAQOpen, setIsFAQOpen] = useState(false);
  const [messages, setMessages] = useState([
    { sender: 'bot', text: '안녕하세요! 무엇을 도와드릴까요?' },
  ]);
  const [input, setInput] = useState('');
  const [loading, setLoading] = useState(false);
  const chatEndRef = useRef(null);

  const toggleFAQ = () => setIsFAQOpen(prev => !prev);

  const handleSend = async () => {
    if (!input.trim()) return;
    const newUserMsg = { sender: 'user', text: input };
    setMessages(prev => [...prev, newUserMsg]);
    setInput('');
    setLoading(true);

    try {
      const res = await fetch('http://localhost:8001/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ user_input: input }),
      });
      const { response } = await res.json();
      setMessages(prev => [...prev, { sender: 'bot', text: response }]);
    } catch {
      setMessages(prev => [
        ...prev,
        { sender: 'bot', text: '⚠️ 서버 연결에 실패했습니다.' },
      ]);
    } finally {
      setLoading(false);
    }
  };

  // 자동 스크롤
  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages, loading]);

  return (
    <div style={styles.page}>
      <Header />

      <div style={styles.chatArea}>
        {messages.map((msg, idx) => (
          <div
            key={idx}
            style={{
              ...styles.message,
              ...(msg.sender === 'user'
                ? styles.userMessage
                : styles.botMessage),
            }}
          >
            {msg.text}
          </div>
        ))}
        {loading && (
          <div style={styles.botMessage}>
            <em style={{ color: '#777' }}>챗봇이 응답을 생성 중입니다...</em>
          </div>
        )}
        <div ref={chatEndRef} />
      </div>

      <div style={styles.inputArea}>
        <input
          style={styles.input}
          placeholder="메시지를 입력하세요..."
          value={input}
          onChange={e => setInput(e.target.value)}
          onKeyDown={e => e.key === 'Enter' && handleSend()}
        />
        <button
          style={styles.sendBtn}
          onClick={handleSend}
          disabled={!input.trim() || loading}
        >
          전송
        </button>
      </div>

      {isFAQOpen && <FAQModal onClose={toggleFAQ} />}
    </div>
  );
};

const styles = {
  page: {
    display: 'flex',
    flexDirection: 'column',
    height: '100vh',
    background: '#f7f9fc',
    fontFamily: "'Pretendard', 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
    color: '#333',
  },
  chatArea: {
    flex: 1,
    padding: '24px',
    overflowY: 'auto',
    display: 'flex',
    flexDirection: 'column',
    gap: '14px',
  },
  message: {
    maxWidth: '75%',
    padding: '14px 18px',
    borderRadius: '18px',
    fontSize: '15px',
    lineHeight: '1.6',
    wordBreak: 'break-word',
    whiteSpace: 'pre-wrap',
    boxShadow: '0 2px 8px rgba(0,0,0,0.06)',
  },
  userMessage: {
    alignSelf: 'flex-end',
    background: 'linear-gradient(135deg, #d9eaff, #aed9fc)',
    color: '#1a1a1a',
    borderTopRightRadius: 4,
  },
  botMessage: {
    alignSelf: 'flex-start',
    background: '#fff',
    color: '#333',
    borderTopLeftRadius: 4,
  },
  inputArea: {
    display: 'flex',
    padding: '16px',
    borderTop: '1px solid #ddd',
    background: '#ffffff',
  },
  input: {
    flex: 1,
    padding: '14px',
    borderRadius: '12px',
    border: '1px solid #ccc',
    outline: 'none',
    fontSize: '15px',
    background: '#fff',
    color: '#333',
    fontFamily: 'inherit',
  },
  sendBtn: {
    marginLeft: '10px',
    padding: '14px 24px',
    border: 'none',
    borderRadius: '12px',
    background: 'linear-gradient(135deg, #9bafd9, #103783)',
    color: '#fff',
    fontWeight: '600',
    fontSize: '15px',
    cursor: 'pointer',
    transition: 'background 0.3s ease',
  },
};

export default ChatBotPage;
