// src/pages/LoginPage.jsx
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

const API_BASE = process.env.REACT_APP_API_BASE || "http://localhost:8001";

export default function LoginPage() {
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  // ✅ 카카오 로그인 성공 콜백 처리 → /home으로 이동
  useEffect(() => {
    const q = new URLSearchParams(window.location.search);
    if (q.get("login") === "success") {
      navigate("/home", { replace: true });
    }
  }, [navigate]);

  const handleKakaoLogin = () => {
    setLoading(true);
    // 백엔드가 로그인 완료 후 되돌아갈 경로. SPA 라우트(/home) 보장.
    // 백엔드에서 세션에 next를 저장하고, 토큰 교환 후 해당 경로로 리다이렉트함.
    const next = "/login?login=success"; // 콜백을 현재 페이지로 받고, 여기서 /home으로 넘김
    window.location.href = `${API_BASE}/authorize?next=${encodeURIComponent(next)}`;
  };

  return (
    <div style={styles.wrap}>
      <form onSubmit={(e) => e.preventDefault()} style={styles.form}>
        <h2 style={styles.title}>로그인</h2>
        <p style={styles.subtitle}>카카오로 안전하게 로그인하세요.</p>

        <button
          type="button"
          onClick={handleKakaoLogin}
          style={styles.kakaoBtn}
          disabled={loading}
        >
          {loading ? "카카오 연결 중..." : "카카오로 로그인"}
        </button>
      </form>
    </div>
  );
}

const styles = {
  wrap: {
    minHeight: "100vh",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    background: "#f7f7f9",
    padding: 24,
  },
  form: {
    width: 360,
    background: "#fff",
    borderRadius: 16,
    boxShadow: "0 8px 24px rgba(0,0,0,0.08)",
    padding: 24,
    display: "flex",
    flexDirection: "column",
    gap: 12,
    fontFamily: "Roboto, 'Noto Sans KR', sans-serif",
  },
  title: { margin: 0, fontSize: 22 },
  subtitle: { margin: "4px 0 12px", color: "#666" },
  kakaoBtn: {
    width: "100%",
    height: 48,
    borderRadius: 10,
    border: "1px solid #FEE500",
    background: "#FEE500",
    fontWeight: 700,
    cursor: "pointer",
  },
};
